import { FETCH_DOGS , SEARCH_DOGS, SORT } from "../actions";

const initialState = {
    dogs: [],
    filteredDogs: [],
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case FETCH_DOGS:
            return{
                ...state,
                dogs: action.payload.data
            }
        case SEARCH_DOGS:
            return{
                ...state,
                dogs: action.payload.data
            }
        case SORT:
        default: return state
    }
}
