import { ASCENDENTE, DESCENDENTE , W_ASCENDENTE , W_DESCENDENTE } from "../../const/sort";
import { FETCH_DOGS , SEARCH_DOGS, SORT_NAME, SORT_WEIGHT } from "../actions";

const initialState = {
    dogs: [],
    filteredDogs: [],
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case FETCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SEARCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SORT_NAME:
            let orderedDogsN = [...state.filteredDogs];
            orderedDogsN.sort((a,b) =>{
                let comparison = a.name.toUpperCase().localeCompare(b.name.toUpperCase());
                return action.payload === DESCENDENTE ? comparison : comparison * -1
                }
            )
            return{
                ...state,
                filteredDogs : orderedDogsN
            }
        case SORT_WEIGHT:
            let orderedDogsW = [...state.filteredDogs];
            let filteredDogsW = orderedDogsW.map((a)=>{
                let numberA = parseInt(a.weightMin)
                let numberB = parseInt(a.weightMax)
                if (typeof numberA==="number" && typeof numberB==="number"){
                    a.avgW=(numberB+numberA)/2;
                    return a
                } else if (typeof numberA==="number" || Number.isNan(numberB)){
                    numberB=numberA;
                    a.avgW=(numberB+numberA)/2;
                    return a
                } else if (typeof numberB==="number" || Number.isNan(numberA)){
                    numberA=numberB;
                    a.avgW=(numberB+numberA)/2;
                    return a
                } else {
                    numberB = numberA = 0
                    return a
                }
            })
            orderedDogsW = filteredDogsW.sort((a,b) => {
                let comparison = a.avgW <= b.avgW ?  -1 : 1
                comparison === -1 ? console.log('menor') : console.log('mayo')
                return action.payload === W_DESCENDENTE ? comparison : !comparison
                })
                console.log(orderedDogsW)
            return {
                    ...state,
                    filteredDogs: orderedDogsW
                }
        default: return state
    }
}
