import { ASCENDENTE, DESCENDENTE } from "../../const/sort";
import { FETCH_DOGS , SEARCH_DOGS, SORT } from "../actions";

const initialState = {
    dogs: [],
    filteredDogs: [],
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case FETCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SEARCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SORT:
            let orderedDogs = [...state.filteredDogs];
            orderedDogs.sort((a,b) =>{
                let comparison = a.name.toUpperCase().localeCompare(b.name.toUpperCase());
                return action.payload === DESCENDENTE ? comparison : comparison * -1
            })
            return{
                ...state,
                filteredDogs : orderedDogs
            }
        default: return state
    }
}
