import { ASCENDENTE, DESCENDENTE , W_ASCENDENTE , W_DESCENDENTE } from "../../const/sort";
import { FETCH_DOGS , SEARCH_DOGS, SORT } from "../actions";

const initialState = {
    dogs: [],
    filteredDogs: [],
}

export default function reducer(state = initialState, action) {
    switch(action.type) {
        case FETCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SEARCH_DOGS:
            return{
                ...state,
                filteredDogs: action.payload.data
            }
        case SORT:
            let orderedDogs = [...state.filteredDogs];
            orderedDogs.sort((a,b) =>{
                function avgWeight (num1,num2) {
                    return ((num1+num2)/2)
                }
                if (a.weightMin && a.weightMax && b.weightMax && b.weightMin){
                    if (action.payload === W_DESCENDENTE) {
                        let comparison = ( avgWeight(a.weightMin,a.weightMax) <= avgWeight(b.weightMin,b.weightMax) )
                        if (comparison) {return 1}
                        else {return -1}
                    } else if ( action.payload === W_ASCENDENTE){
                        let comparison = ( avgWeight(a.weightMin,a.weightMax) >= avgWeight(b.weightMin,b.weightMax) )
                        if (comparison) {return 1}
                        else {return -1}
                } else if ( a.weightMin && b.weightMin){
                    if (action.payload === W_DESCENDENTE) {
                        let comparison = ( a.weightMin <= b.weightMin )
                        if (comparison) {return 1}
                        else {return -1}
                    } else if ( action.payload === W_ASCENDENTE){
                        let comparison = ( a.weightMin >= b.weightMin)
                        if (comparison) {return 1}
                        else {return -1}
                    }
                } else if (a.weightMax && b.weightMax) {
                    if (action.payload === W_DESCENDENTE) {
                        let comparison = ( a.weightMax <= b.weightMin )
                        if (comparison) {return 1}
                        else {return -1}
                    } else if ( action.payload === W_ASCENDENTE){
                        let comparison = ( a.weightMin >= b.weightMin)
                        if (comparison) {return 1}
                        else {return -1}
                    }
                }
            } else {
                let comparison = a.name.toUpperCase().localeCompare(b.name.toUpperCase());
                return action.payload === DESCENDENTE ? comparison : comparison * -1
            }
            })
            return{
                ...state,
                filteredDogs : orderedDogs
            }
        default: return state
    }
}
